con:config.script:
log.info ("  >>>  counter value is " +  testRunner.testCase.getPropertyValue("counter"))
def i = testRunner.testCase.getPropertyValue("counter").toInteger();
def totalNumber = testRunner.testCase.testSuite.getPropertyValue("totalNumber").toInteger();

if (i <= totalNumber/2) {
	def correctValue = testRunner.testCase.testSuite.getPropertyValue(i.toString())
	testRunner.testCase.setPropertyValue("correctVar", correctValue)
	}

log.info ("this is tear down step script")

def i = testRunner.testCase.getPropertyValue("counter").toInteger()
def totalNumber = testRunner.testCase.testSuite.getPropertyValue("totalNumber").toInteger()

if (i <  totalNumber/2) {
	testRunner.testCase.setPropertyValue("counter", (i + 1).toString())
      testRunner.gotoStepByName ("setUpTestStep")
} else {
	testRunner.testCase.setPropertyValue( "counter", new Integer(1).toString())
}

log.info ("  >>>  counter value is " +  testRunner.testCase.getPropertyValue("counter"))
def i = testRunner.testCase.getPropertyValue("counter").toInteger();
def totalNumber = testRunner.testCase.testSuite.getPropertyValue("totalNumber").toInteger();

if (i <= totalNumber/2) {
	def correctValue = testRunner.testCase.testSuite.getPropertyValue(i.toString())
	def incorrectValue = testRunner.testCase.testSuite.getPropertyValue((i+totalNumber/2).toString())
	testRunner.testCase.setPropertyValue("correctVar", correctValue)
	testRunner.testCase.setPropertyValue("incorrectVar", incorrectValue)
}

con:configuration.scriptText:
def words= context.testCase.getPropertyValue("incorrectVar").split()
if (words.length == 1){

def answers = []
def Envelope=new XmlSlurper().parseText(messageExchange.response.responseContent)

Envelope.Body.CheckTextResponse.SpellResult.error.s.each {s -> answers <<s.text() }
answers.each{s -> log.info s}

String varr = context.testCase.getPropertyValue("correctVar")
assert answers.contains(varr)

}

