log.info ("  >>>  counter value is " +  testRunner.testCase.getPropertyValue("counter"))
def i = testRunner.testCase.getPropertyValue("counter").toInteger();
def totalNumber = testRunner.testCase.testSuite.getPropertyValue("totalNumber").toInteger();

if (i < totalNumber/2) {
	def correctValue1 = testRunner.testCase.testSuite.getPropertyValue(i.toString())
	def correctValue2 = testRunner.testCase.testSuite.getPropertyValue((i+1).toString())
	def correctValue3 = testRunner.testCase.testSuite.getPropertyValue((i+2).toString())
	def incorrectValue1 = testRunner.testCase.testSuite.getPropertyValue((i+totalNumber/2).toString())
	def incorrectValue2= testRunner.testCase.testSuite.getPropertyValue((i+totalNumber/2 + 1).toString())
	def incorrectValue3 = testRunner.testCase.testSuite.getPropertyValue((i+totalNumber/2 + 2).toString())
	testRunner.testCase.setPropertyValue("correctVar1", correctValue1)
	testRunner.testCase.setPropertyValue("correctVar2", correctValue2)
	testRunner.testCase.setPropertyValue("correctVar3", correctValue3)
	testRunner.testCase.setPropertyValue("incorrectVar1", incorrectValue1)
	testRunner.testCase.setPropertyValue("incorrectVar2", incorrectValue2)
	testRunner.testCase.setPropertyValue("incorrectVar3", incorrectValue3)
}

con:configuration.scriptText:
for (int i = 1; i < 4; i ++) {

String var = "correctVar" + i.toString()
log.info var
def words= context.testCase.getPropertyValue(var).split()
log.info words.toString()
if (words.length == 1) {

def answers = []
def Envelope=new XmlSlurper().parseText(messageExchange.response.responseContent)

log.info "array:"
Envelope.Body.CheckTextsResponse.ArrayOfSpellResult.SpellResult[i - 1].error.s.each {s -> answers <<s.text() }
answers.each{s -> log.info s}

String varr = context.testCase.getPropertyValue(var)
log.info varr
assert answers.contains(varr)

}
}

